mapper.select:
SELECT count(*) FROM verify

  AND FIND_IN_SET(userid, '${ids}')
  AND `name` LIKE '%${name}%'
  AND `type` = ${type}
  AND `status` = ${status}

SELECT * FROM
( SELECT verify.id, verify.userid, verify.`name`, verify.school, verify.position, verify.technology, verify.introduce, verify.`start`, verify.`end`, verify.experience, verify.detail, verify.photo, verify.type, verify.`status`, verify.createtime, verify.updatetime, `user`.nickname AS username
FROM verify LEFT JOIN `user` ON `user`.uid=verify.userid
WHERE 1 = 1
AND FIND_IN_SET(verify.userid, '${ids}')
AND verify.`name` LIKE '%${name}%'
AND verify.`type` = ${type}
AND verify.`status` = ${status}
LIMIT #{page}, #{limit} ) u
ORDER BY id ${sort}
